export default {
  props: {
    modelValue: {
      type: Object,
      default: () => ({
        nome: '',
        descricao: ''
      }) // Evita undefined
    }
  },
  computed: {
    golpe: {
      get() {
        return this.modelValue || {
          nome: '',
          descricao: ''
        }; // Garante um objeto válido
      },
      set(valor) {
        this.$emit('update:modelValue', valor);
      }
    }
  }
};
